import "core-js/modules/esnext.iterator.map.js";
export default {
  props: ["cart"],
  data() {
    return {
      name: "",
      phone: "",
      checkoutMessage: ""
    };
  },
  methods: {
    checkout() {
      const order = {
        name: this.name,
        phone: this.phone,
        items: this.cart.map(lesson => ({
          lessonId: lesson._id,
          subject: lesson.subject,
          quantity: lesson.quantity || 1 // Include quantity
        }))
      };
      fetch("http://localhost:3000/orders", {
        method: "POST",
        headers: {
          "Content-Type": "application/json"
        },
        body: JSON.stringify(order)
      }).then(() => {
        this.checkoutMessage = "Order placed successfully!";
        this.$emit("clear-cart"); // Emit an event to clear the cart
      }).catch(error => {
        console.error("Checkout error:", error);
        this.checkoutMessage = "Error placing the order. Please try again.";
      });
    },
    removeFromCart(item) {
      this.$emit("update-cart", item); // Emit an event to update the cart
    }
  },
  computed: {
    isCheckoutValid() {
      const nameValid = /^[A-Za-z\s]+$/.test(this.name);
      const phoneValid = /^[0-9]+$/.test(this.phone);
      return nameValid && phoneValid && this.cart.length > 0;
    }
  }
};
